import { useState } from "react";
import moment from "moment/moment";
import fetchWeather from "./weatherfetch";
import "./estyle.css"
// let style = {
//   backgroundColor: "pink",
//   // marginLeft:"30px"
// }

function App() {
  const [query, setQuery] = useState("")
  const [weather, setWeather] = useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let data = await fetchWeather(query)
      setWeather(data)
    }
  }

  return (
    <div>
      <div className="card" style={{ width: "600px" }}>
      <div className="card-head p-3" style={{ backgroundColor: "pink" }}>
          <input value={query} onKeyPress={search} onChange={change} type="text" placeholder="Enter Country Name....!"/>
          <button id="line" style={{ border: "none", width: "30px", backgroundColor: "pink" }}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
      {/* <div className="card">
        <div className="card-body">

          <div className="row g-3 align-items-center">
            <div className="col-auto">
              <input type="text" className="form-control" placeholder="Enter Country Name....!" />
            </div>
            <div className="col-4">
              <span className="form-text">
                <i className="fa-solid fa-arrows-rotate"></i>
              </span>
            </div>
          </div>
        </div> */}
        {weather.main &&
          <div className="card-body">
            <div class="row justify-content-between">
              <div class="col">
                {moment().format('dddd')} , {moment().format('LL')}
              </div>
              <div class="col-4" id="line">
                {weather.weather[0].description}
              </div>
            </div><br />
            <div class="row justify-content-between">
              <div class="col">
                Temperature : {Math.round(weather.main.temp)} &deg;C
              </div>
              <div class="col-4" id="line">
                Humidity : {weather.main.humidity} %
              </div>
            </div><br />
            <div class="row justify-content-between">
              <div class="col">
                Sunrise : {new Date(weather.sys.sunrise * 1000).toLocaleTimeString('en-IN')}
              </div>
              <div class="col-4" id="line">
                Sunset : {new Date(weather.sys.sunset * 1000).toLocaleTimeString('en-IN')}
              </div><br /><br />
              <div class="row justify-content-between">
                <div class="col">
                  Country : {weather.name}
                </div>
                <div class="col-3" id="line">
                  <img style={{backgroundColor:"black",height:"50px",width:"65px"}} src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} />
                </div>
              </div>
            </div>
          </div>
        }
      </div>
    </div>
    // </div>
  )
}
export default App;
