import { useState } from "react";
import moment from "moment/moment";
import fetchWeather from "./weatherfetch";
import "./estyle.css"
// let style = {
//   backgroundColor: "pink",
//   // marginLeft:"30px"
// }

function App() {
  const [query, setQuery] = useState("")
  const [weather, setWeather] = useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let data = await fetchWeather(query)
      setWeather(data)
    }
  }

  return (
    <div>
      <div className="card">
        <div className="card-head p-3">
          <div className="row g-3 align-items-center">
            <div className="col-auto">
              <input value={query} onKeyPress={search} onChange={change} type="text" className="form-control" placeholder="Enter Country Name....!" />
            </div>
            <div className="col-4">
              <span className="form-text">
              <i class="fa-solid fa-arrows-rotate"></i>
              </span>
            </div>
          </div>
          {/* <input value={query} onKeyPress={search} onChange={change} type="text" placeholder="Enter Country Name....!"/> */}
          {/* <button className="line" style={{ border: "none", wclassNameth: "30px", backgroundColor: "pink" }}><i class="fa-solclassName fa-arrows-rotate"></i></button> */}
        </div>
        {weather.main &&
          <div className="card-body">
            <div class="row justify-content-between">
              <div class="col" className="a">
                {moment().format('dddd')} , {moment().format('LL')}
              </div>
              <div class="col" className="line">
                {weather.weather[0].description}
              </div>
            </div><br />
            <div class="row justify-content-between">
              <div class="col" className="a">
                Temperature : {Math.round(weather.main.temp)} &deg;C
              </div>
              <div class="col" className="line">
                Humidity : {weather.main.humclassNameity} %
              </div>
            </div><br />
            <div class="row justify-content-between">
              <div class="col" className="a">
                Sunrise : {new Date(weather.sys.sunrise * 1000).toLocaleTimeString('en-IN')}
              </div>
              <div class="col" className="line">
                Sunset : {new Date(weather.sys.sunset * 1000).toLocaleTimeString('en-IN')}
              </div><br /><br />
              <div class="row justify-content-between">
                <div class="col" className="a">
                  Country : {weather.name}
                </div>
                <div class="col" className="line">
                  <img style={{ backgroundColor: "black", height: "50px", wclassNameth: "65px" }} src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} />
                </div>
              </div>
            </div>
          </div>
        }
      </div>
    </div>
    // </div>
  )
}
export default App;
