import { useState } from "react";
import moment from "moment/moment";
import fetchWeather from "./weatherfetch";

// let style = {
//   backgroundColor: "pink",
//   // marginLeft:"30px"
// }

function App() {
  const [query, setQuery] = useState("")
  const [weather, setWeather] = useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let data = await fetchWeather(query)
      setWeather(data)
    }
  }

  return (
    <div>
      <div className="card" style={{ width: "600px" }}>
        <div className="card-head p-3" style={{ backgroundColor: "pink" }}>
          <input value={query} onKeyPress={search} onChange={change} type="text" placeholder="Enter Country Name....!" style={{ width: "250px" }} />
          <button class="float-end" style={{ border: "none", width: "30px", backgroundColor: "pink" }}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
        {weather.main &&
          <div className="card-body">
            <div class="row justify-content-between">
              <div class="col-5">
              {moment().format('dddd')} , {moment().format('LL')}
              </div>
              <div class="col-2">
              {weather.weather[0].description}
              </div>
            </div><br/>
            <div class="row justify-content-between">
              <div class="col-5">
                  Temperature : {Math.round(weather.main.temp)} &deg;C
              </div>
              <div class="col-3">
                  Humidity : {weather.main.humidity} %
              </div>
            </div><br/>
            <div class="row justify-content-between">
              <div class="col-4">
                  Sunrise : {new Date(weather.sys.sunrise * 1000).toLocaleTimeString('en-IN')}
              </div>
              <span class="col-4">
                  Sunset : {new Date(weather.sys.sunset * 1000).toLocaleTimeString('en-IN')}
              </span>
            </div>
          </div>
        }
      </div>
    </div>
  )
}
export default App;
