import { useState } from "react";
import moment from "moment/moment.js";
import fetchWeather from "./fetchweather.js";

let style = {
  backgroundColor: "pink"
}

function App() {
  const [query, setQuery] = useState("")
  const [weather,setWeather]=useState({})  // {} - json format


  const change = (e) => {
    setQuery(e.target.data)
    console.log(e.target.data)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let value = await fetchWeather(query)
      setWeather(value)
      console.log(value)
    }
  }

  return (
    <div>
      <div className="card" style={{ width: "500px" }}>
        <div className="input-group" style={style}>
          <input value={query} onChange={change} type="text" placeholder="Enter Country Name....!" style={{ width: "300px" }} onKeyPress={search} />
          <button style={{ border: "none", width: "30px", backgroundColor: "pin" }}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
        <div className="card-body">
          {weather.main &&
            <div>
              {moment().format('dddd')} , {moment().format("MMM Do YY")}
            </div>
          }
        </div>
      </div>
    </div>
  )
}
export default App;
