import { useState } from "react";
import fetchWeather from "./weatherfetch";

let style = {
  backgroundColor: "pink"
}

function App() {
  const [query, setQuery] = useState("")
  const [weather,setWeather]=useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search=(e)=>{
    if(e.code=="Enter"){
      let data=fetchWeather(query)
      setWeather(data)
    }
  }

  return (
    <div>
      <div className="card" style={{ width: "500px" }}>
        <div className="input-group" style={style}>
          <input value={query} onKeyPress={search} onChange={change} type="text" placeholder="Enter Country Name....!" style={{ width: "300px" }}/>
          <button style={{ border: "none", width: "30px", backgroundColor: "pin" }}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
        {/* <div className="card-body">
          {weather.main &&
            <div>
              {moment().format('dddd')} , {moment().format("MMM Do YY")}
            </div>
          }
        </div> */}
      </div>
    </div>
  )
}
export default App;
