import { useState } from "react";
import fetchWeather from "./weatherfetch";

// let style = {
//   backgroundColor: "pink",
//   // marginLeft:"30px"
// }

function App() {
  const [query, setQuery] = useState("")
  const [weather, setWeather] = useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let data = await fetchWeather(query)
      setWeather(data)
    }
  }

  return (
    <div>
      <div className="card" style={{ width: "600px" }}>
        <div className="row p-3" style={{backgroundColor:"pink"}}>
          <div className="col-1">
          <input value={query} onKeyPress={search} onChange={change} type="text" placeholder="Enter Country Name....!" style={{ width: "250px" }} />
          <button style={{ border: "none", width: "30px",backgroundColor:"pink"}}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
        </div>
        <div className="row justify-content-around">
          <div className="col-4">
            One of two columns
          </div>
          <div className="col-4">
            One of two columns
          </div>
        </div>
      </div>
    </div>
  )
}
export default App;
