import axios from "axios"


let APP_KEY="984d931e6dc326caa8b542f532f90f30"
let URL="https://api.openweathermap.org/data/2.5/weather"

// in query place the state values are passing
// here axios used to get data from apikey

const fetchWeather=async(query)=>{
    let {data}=await axios .get(URL,{
        params:{
            // q:"bangalore"   // in this we getting only mented city current weather values
            // for that we use callback method
            q:query,
            APPID:APP_KEY,
            units:"metric"
        }
    })
    return data
}
export default fetchWeather;
