import { useState } from "react";
import moment from "moment/moment";
import fetchWeather from "./weatherfetch";
import "./estyle.css"
// let style = {
//   backgroundColor: "pink",
//   // marginLeft:"30px"
// }

function App() {
  const [query, setQuery] = useState("")
  const [weather, setWeather] = useState({})

  const change = (e) => {
    setQuery(e.target.value)
    // console.log(e.target.value)
  }

  const search = async (e) => {
    if (e.code == "Enter") {
      let data = await fetchWeather(query)
      setWeather(data)
    }
  }

  const reload=()=>{
    window.location.reload()
  }

  return (
    <div>
      <div className="card">
        <div className="card-head p-3">
          <div className="row">
            <div className="col">
              <input value={query} onKeyPress={search} onChange={change} type="text" className="form-control" placeholder="Enter Country Name....!" />
            </div>
            <div className="col">
          <button className="button" onClick={reload}><i class="fa-solid fa-arrows-rotate"></i></button>
        </div>
          </div>
        </div> 
        {weather.main &&
          <div className="card-body">
            <div className="row">
              <div class="col">
                {moment().format('dddd')} , {moment().format('LL')}
              </div>
              <div className="col">
                <p className="button">{weather.weather[0].description}</p>
              </div>
            </div><br />
            <div class="row">
              <div class="col">
                Temperature : {Math.round(weather.main.temp)} &deg;C
              </div>
              <div class="col">
              <p className="button">Humidity : {weather.main.humidity} %</p>
              </div>
            </div><br />
            <div class="row">
              <div class="col">
                Sunrise : {new Date(weather.sys.sunrise * 1000).toLocaleTimeString('en-IN')}
              </div>
              <div class="col">
              <p className="button">Sunset : {new Date(weather.sys.sunset * 1000).toLocaleTimeString('en-IN')}</p>
              </div><br /><br />
              <div class="row">
                <div class="col">
                  Country : {weather.name}
                </div>
                <div class="col">
                <p className="button"><img style={{ backgroundColor: "black", height: "50px", width: "65px" }} src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} /></p>
                </div>
              </div>
            </div>
          </div>
        }
      </div>
    </div>
    // </div>
  )
}
export default App;
